import React from "react";
import styled from "styled-components";
import { saturate, darken } from "polished";
import PropTypes from "prop-types";

const TextInput = () => {
  const { labelId, label, placeholder } = this.props;
  return (
    <Wrapper>
      <Container>
        <Label for={labelId}>{label}</Label>
        <Input id={labelId} placeholder={placeholder} type="text" />
      </Container>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 350px;
  position: relative;
  margin: 30px;
`;

const Container = styled.div`
  background: ${saturate(0.3, "#fff")};
  padding: 16px 0 5px 0;
  width: 350px;
  border-bottom: 2px solid ${saturate(0.0, "#A4A2BC")} !important;
  position: relative;
`;

const Input = styled.input`
  width: 100%;
  border: 0;
  outline: none;
  line-height: 20px;
  font-size: 22px;
  padding: 9px 0 10px 0px;
  font-family: inherit;
  background: none;
  color: #151924;
  caret-color: #332e4b;

  &::placeholder {
    color: #a4a2bc;
  }
`;

const Label = styled.label`
  line-height: 24px;
  font-size: 16px;
  font-weight: 500;
  transition: all 0.3s ease;
  color: ${darken(0.4, "#332E4B")};
`;

TextInput.propTypes = {
  labelId: PropTypes.string.isRequired,
  label: PropTypes.string.isRequired,
  placeholder: PropTypes.string.isRequired,
};

export default TextInput;
