import React, { useState } from "react";
import styled from "styled-components";

import TextInput from "./TextInput";
import EmailInput from "./EmailInput";
import PhoneInput from "./PhoneInput";
import RadioBtn from "./RadioBtn";
import MessageInput from "./MessageInput";
import Button from "./Button";

const RightColumn = () => {
  // initialize state to hold validity of form fields
  const [fieldState, setFieldState] = useState({
    firstname: false,
    lastname: false,
    email: false,
    phone: false,
    message: false,
  });

  const [fieldValue, setFieldValue] = useState({
    firstname: "",
    lastname: "",
    email: "",
    phone: "",
    message: "",
  });

  const [radioState, setRadioState] = useState({});

  const onValueChange = (event) =>
    setRadioState({ selectedOption: event.target.value });

  const formSubmit = (event) => {
    event.preventDefault();
    console.log(radioState);
  };

  // higher-order function that returns a state change watch function
  // sets the corresponding state property to true if the form field has no errors
  // const fieldStateChanged = (field) => (state) =>
  //   setFieldState({ [field]: state.errors.length === 0 });
  const fieldStateChanged = (field) => (state) => {
    setFieldState({ [field]: state.errors.length === 0 });
    setFieldValue({ [field]: state.value });
  };

  const firstNameChanged = fieldStateChanged("firstname");
  const lastNameChanged = fieldStateChanged("lastname");
  const emailChanged = fieldStateChanged("email");
  const phoneChanged = fieldStateChanged("phone");
  const messageChanged = fieldStateChanged("message");

  const validateName = (value, label) => {
    const regex = /^[a-z]{2,}$/i;
    if (!regex.test(value)) throw new Error(`${label} is invalid`);
  };

  const { fullname } = fieldState;
  const { firstname, lastname } = fieldValue;
  console.log(firstname);
  console.log(lastname);
  // const formValidated = fullname && email && password;

  return (
    <Wrapper>
      <form>
        <FirstRow>
          <TextInput
            labelId="firstname"
            label="First Name"
            placeholder="John Doe"
            type="text"
            validator={validateName}
            onStateChanged={firstNameChanged}
            required
          />
          <TextInput
            labelId="lastname"
            label="Last Name"
            placeholder="John Doe"
            type="text"
            validator={validateName}
            onStateChanged={lastNameChanged}
            required
          />
        </FirstRow>
        <SecondRow>
          <EmailInput
            labelId="email"
            label="Mail"
            placeholder="mymail@gmail.com"
            onStateChanged={emailChanged}
            required
          />
          <PhoneInput
            labelId="phone"
            label="Phone"
            placeholder="+971 50 267 5078"
            onStateChanged={phoneChanged}
            required
          />
        </SecondRow>
        <Description>What type of website do you need?</Description>
        <ThirdRow>
          <RadioBtn
            id="choice1"
            value="Web Design"
            label="Web Design"
            radioChecked={radioState.selectedOption === "Web Design"}
            radioChanged={onValueChange}
          />
          <RadioBtn
            id="choice2"
            value="Web Development"
            label="Web Development"
            radioChecked={radioState.selectedOption === "Web Development"}
            radioChanged={onValueChange}
          />
          <RadioBtn
            id="choice3"
            value="Logo Design"
            label="Logo Design"
            radioChecked={radioState.selectedOption === "Logo Design"}
            radioChanged={onValueChange}
          />
          <RadioBtn id="choice4" value="Other" label="Other" />
        </ThirdRow>
        <FourthRow>
          <MessageInput
            label="message"
            labelId="message"
            placeholder="Write your message"
            onStateChanged={messageChanged}
            required
          />
        </FourthRow>
        <LastRow>
          <Button />
        </LastRow>
      </form>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 71%;
  padding: 30px 60px 30px 60px;

  @media (max-width: 768px) {
    padding: 30px 3px 30px 3px;
    width: 100%;
  }
`;

const FirstRow = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-between;

  @media (max-width: 768px) {
    flex-direction: column;
    justify-content: space-between;
    height: 180px;
  }
`;

const SecondRow = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-between;
  margin-top: 30px;

  @media (max-width: 768px) {
    flex-direction: column;
    justify-content: space-between;
    height: 180px;
  }
`;

const ThirdRow = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-between;
  margin-top: 30px;

  @media (max-width: 768px) {
    flex-direction: column;
    justify-content: space-between;
    height: 150px;
  }
`;

const Description = styled.div`
  color: #0e054d;
  font-weight: 700;
  margin-top: 30px;
`;

const FourthRow = styled.div`
  width: 100%;
  margin-top: 30px;
`;

const LastRow = styled.div`
  width: 100%;
  margin-top: 40px;
  text-align: right;

  @media (max-width: 768px) {
    text-align: left;
  }
`;

export default RightColumn;
