import React, { useState } from "react";
import styled from "styled-components";
import { saturate, darken } from "polished";
import PropTypes from "prop-types";

const TextInput = (props) => {
  const { inputState, setInputState } = useState({
    value: "",
    dirty: false,
    errors: [],
  });

  const handleChange = (e) => {
    e.preventDefault();

    // destructure props - assign default dummy functions to validator and onStateChanged props
    const {
      label,
      required = false,
      validator = (f) => f,
      onStateChanged = (f) => f,
    } = props;

    const value = e.target.value;
    const isEmpty = value.length === 0;
    const requiredMissing = inputState.dirty && required && isEmpty;

    let errors = [];

    if (requiredMissing) {
      // if required and is empty, add required error to state
      errors = [...errors, `${label} is required`];
    } else if ("function" === typeof validator) {
      try {
        validator(value);
      } catch (e) {
        // if validator throws error, add validation error to state
        errors = [...errors, e.message];
      }
    }

    // update state and call the onStateChanged callback fn after the update
    // dirty is only changed to true and remains true on the after the first state update
    setInputState(
      ({ dirty = false }) => ({ value, errors, dirty: !dirty || dirty }),
      () => onStateChanged(inputState)
    );
  };
  const { labelId, label, placeholder } = props;
  return (
    <Wrapper>
      <Container>
        <Label for={labelId}>{label}</Label>
        <Input id={labelId} placeholder={placeholder} type="text" />
      </Container>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 350px;
  position: relative;
  margin: 30px;
`;

const Container = styled.div`
  background: ${saturate(0.3, "#fff")};
  padding: 16px 0 5px 0;
  width: 350px;
  border-bottom: 2px solid ${saturate(0.0, "#A4A2BC")} !important;
  position: relative;
`;

const Input = styled.input`
  width: 100%;
  border: 0;
  outline: none;
  line-height: 20px;
  font-size: 22px;
  padding: 9px 0 10px 0px;
  font-family: inherit;
  background: none;
  color: #151924;
  caret-color: #332e4b;

  &::placeholder {
    color: #a4a2bc;
  }
`;

const Label = styled.label`
  line-height: 24px;
  font-size: 16px;
  font-weight: 500;
  transition: all 0.3s ease;
  color: ${darken(0.4, "#332E4B")};
`;

TextInput.propTypes = {
  labelId: PropTypes.string.isRequired,
  label: PropTypes.string.isRequired,
  placeholder: PropTypes.string.isRequired,
};

export default TextInput;
