import React from "react";
import styled from "styled-components";
import { saturate } from "polished";

const TextInput = (props) => {
  return (
    <Wrapper>
      <Container>
        <Label for={props.labelId}>{props.label}</Label>
        <Input id={props.labelId} placeholder={props.placeholder} type="text" />
      </Container>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 350px;
  position: relative;
  margin: 30px;
`;

const Container = styled.div`
  background: ${saturate(0.3, "#fff")};
  padding: 16px 0 5px 0;
  width: 350px;
  border-bottom: 2px solid ${saturate(0.0, "#A4A2BC")} !important;
  position: relative;
`;

const Input = styled.input`
  width: 100%;
  border: 0;
  outline: none;
  line-height: 20px;
  font-size: 25px;
  padding: 9px 0 10px 0px;
  font-family: inherit;
  background: none;
  color: #151924;
  caret-color: #332e4b;
`;

const Label = styled.label`
  position: relative;
  z-index: 1;
  left: 16px;
  top: 50%;
  line-height: 24px;
  font-size: 16px;
  margin: -12px 0 0 0;
  transition: all 0.3s ease;
`;

export default TextInput;
