import React, { useState } from "react";
import styled from "styled-components";

import TextInput from "./TextInput";
import EmailInput from "./EmailInput";
import PhoneInput from "./PhoneInput";
import RadioBtn from "./RadioBtn";

const RightColumn = () => {
  // initialize state to hold validity of form fields
  const [fieldState, setFieldState] = useState({
    firstname: false,
    lastname: false,
    email: false,
    phone: false,
  });

  // higher-order function that returns a state change watch function
  // sets the corresponding state property to true if the form field has no errors
  const fieldStateChanged = (field) => (state) =>
    setFieldState({ [field]: state.errors.length === 0 });

  const firstNameChanged = fieldStateChanged("firstname");
  const lastNameChanged = fieldStateChanged("lastname");
  const emailChanged = fieldStateChanged("email");
  const phoneChanged = fieldStateChanged("phone");

  const validateName = (value, label) => {
    const regex = /^[a-z]{2,}$/i;
    if (!regex.test(value)) throw new Error(`${label} is invalid`);
  };

  const { fullname } = fieldState;
  // const formValidated = fullname && email && password;

  return (
    <Wrapper>
      <FirstRow>
        <TextInput
          labelId="firstname"
          label="First Name"
          placeholder="John Doe"
          type="text"
          validator={validateName}
          onStateChanged={firstNameChanged}
          required
        />
        <TextInput
          labelId="lastname"
          label="Last Name"
          placeholder="John Doe"
          type="text"
          validator={validateName}
          onStateChanged={lastNameChanged}
          required
        />
      </FirstRow>
      <SecondRow>
        <EmailInput
          labelId="email"
          label="Mail"
          placeholder="mymail@gmail.com"
          onStateChanged={emailChanged}
          required
        />
        <PhoneInput
          labelId="phone"
          label="Phone"
          placeholder="+971 50 267 5078"
          onStateChanged={phoneChanged}
          required
        />
      </SecondRow>
      <ThirdRow>
        <RadioBtn />
      </ThirdRow>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 71%;
  padding: 30px 60px 30px 60px;
`;

const FirstRow = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

const SecondRow = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  margin-top: 40px;
`;

const ThirdRow = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  margin-top: 40px;
`;

const RadioLabel = styled.label`
  margin-right: 15px;
  line-height: 32px;
`;
const RadioWrapper = styled.div`
  display: flex;
  align-items: flex-start;
  margin-bottom: 5px;
`;

export default RightColumn;
